import{_ as e,c as r,o as a,a3 as l}from"./chunks/framework.5IuD8hAv.js";const m=JSON.parse('{"title":"Introduction","description":"","frontmatter":{},"headers":[],"relativePath":"en/guide/index.md","filePath":"en/guide/index.md","lastUpdated":1764772702000}'),i={name:"en/guide/index.md"};function o(h,t,n,d,s,g){return a(),r("div",{"data-pagefind-body":!0,"data-pagefind-meta":"date:1764772702000"},[...t[0]||(t[0]=[l('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h1><p>HaloLight is a multi-framework enterprise-level admin dashboard solution.</p><h2 id="what-is-halolight" tabindex="-1">What is HaloLight <a class="header-anchor" href="#what-is-halolight" aria-label="Permalink to &quot;What is HaloLight&quot;">​</a></h2><p>HaloLight follows the philosophy of &quot;one design specification, multiple framework implementations&quot;, providing developers with a unified Admin Dashboard experience. Whether you use React, Vue, Angular, or other modern frameworks, you&#39;ll get consistent functionality and design.</p><h2 id="core-features" tabindex="-1">Core Features <a class="header-anchor" href="#core-features" aria-label="Permalink to &quot;Core Features&quot;">​</a></h2><h3 id="draggable-dashboard" tabindex="-1">Draggable Dashboard <a class="header-anchor" href="#draggable-dashboard" aria-label="Permalink to &quot;Draggable Dashboard&quot;">​</a></h3><p>Custom Dashboard system based on Grid Layout, supporting:</p><ul><li>Widget drag and drop arrangement</li><li>Responsive layout adaptation</li><li>Layout state persistence</li></ul><h3 id="permission-control" tabindex="-1">Permission Control <a class="header-anchor" href="#permission-control" aria-label="Permalink to &quot;Permission Control&quot;">​</a></h3><p>Complete RBAC permission management system:</p><ul><li>Fine-grained permission control (page/button level)</li><li>Wildcard permission matching (<code>users:*</code>, <code>*</code>)</li><li>Dynamic menu rendering</li></ul><h3 id="theme-system" tabindex="-1">Theme System <a class="header-anchor" href="#theme-system" aria-label="Permalink to &quot;Theme System&quot;">​</a></h3><p>Rich visual customization capabilities:</p><ul><li>11 skin presets</li><li>Light/Dark mode switching</li><li>View Transitions animation effects</li></ul><h3 id="component-library" tabindex="-1">Component Library <a class="header-anchor" href="#component-library" aria-label="Permalink to &quot;Component Library&quot;">​</a></h3><p>Based on shadcn/ui design system:</p><ul><li>30+ beautiful UI components</li><li>Complete form/table solutions</li><li>Highly customizable</li></ul><h2 id="framework-implementations" tabindex="-1">Framework Implementations <a class="header-anchor" href="#framework-implementations" aria-label="Permalink to &quot;Framework Implementations&quot;">​</a></h2><p>All framework versions have been implemented and deployed (preview links available in respective repository READMEs). Current reference implementations:</p><ul><li>Next.js 14：<a href="https://halolight.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a> · <a href="https://github.com/halolight/halolight" target="_blank" rel="noreferrer">GitHub</a></li><li>Vue 3.5：<a href="https://halolight-vue.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a> · <a href="https://github.com/halolight/halolight-vue" target="_blank" rel="noreferrer">GitHub</a></li></ul><table tabindex="0"><thead><tr><th>Framework</th><th>Status</th><th>Preview</th><th>Repository</th></tr></thead><tbody><tr><td>Next.js 14</td><td>✅ Deployed</td><td><a href="https://halolight.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Vue 3.5</td><td>✅ Deployed</td><td><a href="https://halolight-vue.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-vue" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Angular 21</td><td>✅ Deployed</td><td><a href="https://halolight-angular.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-angular" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Nuxt 4</td><td>✅ Deployed</td><td><a href="https://halolight-nuxt.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-nuxt" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>SvelteKit 2</td><td>✅ Deployed</td><td><a href="https://halolight-svelte.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-svelte" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Astro 5</td><td>✅ Deployed</td><td><a href="https://halolight-astro.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-astro" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Solid.js</td><td>✅ Deployed</td><td><a href="https://halolight-solid.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-solid" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Qwik</td><td>✅ Deployed</td><td><a href="https://halolight-qwik.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-qwik" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Remix</td><td>✅ Deployed</td><td><a href="https://halolight-remix.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-remix" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Preact</td><td>✅ Deployed</td><td><a href="https://halolight-preact.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-preact" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Lit</td><td>✅ Deployed</td><td><a href="https://halolight-lit.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-lit" target="_blank" rel="noreferrer">GitHub</a></td></tr><tr><td>Fresh (Deno)</td><td>✅ Deployed</td><td><a href="https://halolight-fresh.h7ml.cn/" target="_blank" rel="noreferrer">Preview</a></td><td><a href="https://github.com/halolight/halolight-fresh" target="_blank" rel="noreferrer">GitHub</a></td></tr></tbody></table><h2 id="tech-stack" tabindex="-1">Tech Stack <a class="header-anchor" href="#tech-stack" aria-label="Permalink to &quot;Tech Stack&quot;">​</a></h2><p>All framework versions share the following tech stack:</p><ul><li><strong>TypeScript</strong> - Type safety</li><li><strong>Tailwind CSS</strong> - Atomic CSS</li><li><strong>shadcn/ui</strong> - UI component library</li><li><strong>TanStack Query</strong> - Server state management</li><li><strong>ECharts</strong> - Chart visualization</li><li><strong>Mock.js</strong> - Data simulation</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/halolight/en/guide/getting-started">Quick Start</a> - Choose a framework version to start</li><li><a href="/halolight/en/guide/nextjs">Next.js Version</a> - Learn more about Next.js implementation</li><li><a href="/halolight/en/guide/vue">Vue Version</a> - Learn more about Vue implementation</li><li><a href="/halolight/en/development/">Development Docs</a> - Learn about shared design specifications</li></ul>',26)])])}const p=e(i,[["render",o]]);export{m as __pageData,p as default};
